import type { Metadata } from "next";
import "./globals.css"; // import TailwindCSS ho·∫∑c CSS global
import { AuthProvider } from "@/contexts/AuthContext";
import { CartProvider } from "@/contexts/CartContext";
import { Toaster } from "react-hot-toast";
import { serverApi } from "@/lib/server-api";

export const metadata: Metadata = {
  title: "My Store",
  description: "Website b√°n h√†ng demo v·ªõi Next.js",
};

interface User {
  id: string;
  username: string;
  email: string;
  firstName: string;
  lastName: string;
  authenticated: boolean;
  roles: string[];
}

/**
 * Root Layout - Server Component
 * 
 * ‚ú® Fetch user data t·ª´ server m·ªôt l·∫ßn cho to√†n b·ªô app
 * ‚Üí Tr√°nh loading state khi F5
 * 
 * ‚ö†Ô∏è CH·ªà wrap providers, KH√îNG wrap Header/Footer
 * ‚Üí M·ªói route group c√≥ layout ri√™ng
 */
export default async function RootLayout({
  children,
}: {
  children: React.ReactNode;
}) {
  // üöÄ Fetch user data t·ª´ server (SSR) - ch·ªâ fetch 1 l·∫ßn
  const initialUser = await serverApi.get<User>('/users/me');
  
  console.log('üåç [RootLayout] Initial user data:', initialUser);

  return (
    <html lang="vi">
      <body className="bg-gray-50 text-gray-900">
        {/* ‚ú® Pass initialData to AuthProvider */}
        <AuthProvider initialData={initialUser}>
          <CartProvider>
            {/* ‚úÖ Children s·∫Ω l√† nested layouts (shop/admin/staff) */}
            {children}
            
            {/* Toast notifications */}
            <Toaster
              position="top-right"
              reverseOrder={false}
              gutter={16}
              containerStyle={{
                top: '6rem',
                right: '1rem',
              }}
            />
          </CartProvider>
        </AuthProvider>
      </body>
    </html>
  );
}